package Loops_Java;

public class Mutplication_HW2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j; 
	    for(i = 1 ; i<=5; i++) {
	    	for(j = 1 ; j<=10; j++) {
	    		int result = i*j;
	    		System.out.println(i +"x" + j + "=" + result);
	    	}
	    }

	}

}
